import { View, Text, StyleSheet, TextInput, TouchableOpacity } from 'react-native'
import React, { useContext, useState } from 'react'
import axios from 'axios'
import {useNavigate} from "react-router-dom"
export default function Login() {
    const [email,setEmail]=useState()
    const [password,setPassword]=useState()
    const navigate=useNavigate()

    const handleForm=async()=>{
        console.log(email,password)
        await axios.post('http://192.168.239.38:3000/signin',{email,password}).then((res)=>{
            console.log(res.data)
            if(res.data.status===200){
                setToken(res.data.token)
                navigate('profile')
            }else{
                console.log(res.data.message)
            }
        }).catch((err)=>{
            console.log(err.data);
        })
}
  return (
    <View style={styles.Authen}>
    <Text style={{fontSize:32,fontWeight:"700",textAlign:"center",marginVertical:"22"}}>Login Page</Text>
    <View style={styles.field}>
    <TextInput placeholder='Email' onChangeText={e=>setEmail(e)} style={styles.inputs}></TextInput>
    <TextInput placeholder='Password' onChangeText={e=>setPassword(e)} style={styles.inputs}></TextInput>
    <TouchableOpacity style={styles.btn} onPress={handleForm}>
        <Text style={{textAlign:"center",fontSize:26,color:"white"}}>Login</Text>
    </TouchableOpacity>

    <View style={{flexDirection:"row",marginVertical:12}}>
                 <Text style={{fontSize:22}}>
                  Don't have an account? 
                 </Text>
                  <TouchableOpacity onPress={()=>navigate.navigate('register')} >
                  <Text style={{fontSize:22,color:"blue"}}>Register</Text>
                  </TouchableOpacity> </View>
    </View>
    </View>
  )
}
const styles=StyleSheet.create({
    Authen:{
        height:"100%",
        justifyContent:"center",
        alignContent:"center"
    },
    field:{
        width:"90%",
        marginHorizontal:"auto"
    },
    inputs:{
        borderWidth:1,
        borderColor:'grey',
        borderRadius:8,
        height:60,
        marginVertical:12,
        fontSize:22
    },
    btn:{
        backgroundColor:"skyblue",
        height:60,
        borderWidth:1,
        borderColor:'grey',
        borderRadius:8,
        justifyContent:"center"
    }
})
